package Test;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Arithmetic -- +,-,*,/,% 
		 * Operation
		 * Relational
		 * Bitwise
		 */
		
		
		// Arithmetic 
		int A1 = 3, A2 = 2;
		
		int Add = A1+A2;
		int Subs = A1-A2;
		int Mul = A1*A2;
		double Div = (double)A1/A2;
		int Mod = A1%A2;
		
		System.out.println(Add);
		System.out.println(Subs);
		System.out.println(Mul);
		System.out.println(Div);
		System.out.println(Mod);
		
		//Shortcut
		//A1++; it will incriment the value of A i.e, 4
		// 57.26

	}

	
	

}
