package Test;

public class Use_Variable_Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a  = 10;
		int b  = 1;
		int c = a+b;
		
		System.out.println(c);
		
		if(c >= 33) {
			System.out.println("Addition is correct");
		}
		else {
			System.out.println("Not correct");
		}
		
		

	}

}
